                this.txtVal = this.value8.length;
            },
            //查询医生详细信息
            async doctorxinxi(){
                var that = this;
                var url ="docPerCen/queryDoctorInfo";
                var data ={

                }
                var res  = await http.post(that,url,data);

                if(res.errcode==0){
                    that.docxiang=res.data;
                    localStorage.setItem('ysdoccode',res.data[0].doctorno);
                    localStorage.setItem('ysdocname',res.data[0].name);
                    if(res.data[0].head_image!=undefined&&res.data[0].head_image!=null){
                        that.headimgurl=res.data[0].head_image;
                    }else{
                        if(localStorage.getItem("avatar")!=null&&localStorage.getItem("avatar")!=undefined){
                            var headimgurl= localStorage.getItem("avatar");
                            that.headimgurl  = headimgurl
                        }
                    }
                    if(res.data[0].expertjob==undefined||res.data[0].expertjob==null){
                        that.docxiang[0].expertjob='无'
                    }
                }
            },
